import { useEffect, useState } from "react";
import { API } from "./api";

export default function AdminBookings() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(API + "/admin/bookings")
      .then(res => res.json())
      .then(data => {
        setBookings(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div style={{ padding: 20 }}>Loading bookings...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin â€“ All Bookings</h2>

      {bookings.length === 0 ? (
        <p>No bookings yet.</p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>User</th>
              <th>Destination</th>
              <th>Date</th>
              <th>People</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((b, i) => (
              <tr key={i}>
                <td>{b.username}</td>
                <td>{b.destination}</td>
                <td>{b.travel_date}</td>
                <td>{b.people}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
