import { useNavigate } from "react-router-dom";

export default function Home() {
  const nav = useNavigate();
  const username = localStorage.getItem("username") || "Traveler";
  const isAdmin = localStorage.getItem("is_admin") === "1";

  const logout = () => {
    localStorage.clear();
    nav("/");
  };

  return (
    <>
      <div className="navbar">
        <h1>✈️ TravelBuddy</h1>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <span style={{ fontSize: 14 }}>Hi, {username}</span>
          <button onClick={logout}>Logout</button>
        </div>
      </div>

      <div className="page">
        <h2 className="section-title">Plan your dream vacation</h2>
        <p style={{ marginBottom: 20 }}>
          Browse beautiful destinations and book your next trip in a few clicks.
        </p>

        <button
          className="btn-primary"
          onClick={() => nav("/pick")}
          style={{ padding: "10px 20px" }}
        >
          Explore Destinations
        </button>

        {isAdmin && (
          <button
            onClick={() => nav("/admin")}
            style={{
              marginLeft: 12,
              padding: "10px 20px",
              borderRadius: 999,
              border: "1px solid #ff6b35",
              background: "#fff",
              color: "#ff6b35",
              cursor: "pointer"
            }}
          >
            Admin: View Bookings
          </button>
        )}
      </div>
    </>
  );
}
