import { useState, useEffect } from "react";
import { API } from "./api";

export default function BookingPage() {
  const params = new URLSearchParams(window.location.search);
  const destId = params.get("id");

  const [date, setDate] = useState("");
  const [dest, setDest] = useState(null);

  useEffect(() => {
    // Fetch destination details (simple)
    fetch(API + "/destinations")
      .then(res => res.json())
      .then(data => {
        const d = data.find(x => String(x.id) === String(destId));
        setDest(d);
      });
  }, [destId]);

  const book = async () => {
    const userId = localStorage.getItem("uid");
    if (!userId) return alert("Please login first");
    const res = await fetch(API + "/bookings", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id: userId, destination_id: destId, date })
    });

    const data = await res.json();
    if (data.success) alert("Booking Successful! Booking ID: " + data.bookingId);
    else alert("Booking failed");
  };

  return (
    <div style={{padding:20}}>
      <h2>Book Destination</h2>
      {dest && <div><strong>{dest.name} — {dest.country}</strong><p>Price: ₹{dest.price}</p></div>}
      <input type="date" onChange={e => setDate(e.target.value)} />
      <br /><br />
      <button onClick={book}>Confirm Booking</button>
    </div>
  );
}
