import { useEffect, useState } from "react";
import { API } from "./api";

export default function AdminBookings() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(API + "/bookings/admin")
      .then(res => res.json())
      .then(data => {
        setBookings(data.bookings || []);
        setLoading(false);
      })
      .catch(err => {
        console.error("Fetch error:", err);
        setLoading(false);
      });
  }, []);

  // ✅ format date helper
  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    return new Date(dateStr).toISOString().split("T")[0];
  };

  if (loading) {
    return <div style={{ padding: 20 }}>Loading bookings...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin – Booked Trips</h2>

      {bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ marginTop: 10, width: "100%" }}>
          <thead>
            <tr>
              <th>User</th>
              <th>Destination</th>
              <th>Date</th>
              <th>People</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(b => (
              <tr key={b.id}>
                <td>{b.username}</td>
                <td>{b.destination}</td>
                <td>{formatDate(b.travel_date)}</td>
                <td>{b.people}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
